  startAutomaticMovement() {
    //ativar o som do camião
    this.truckAudio.play();
    let x3 = document.getElementById("OptionStartAutomaticDelivery");
    let z = document.getElementById("OptionMakeDelivery");

    if (x3 != null && z != null) {
      if (x3.style.display === "block" && z.style.display === "none") {
        z.style.display = "block";
        x3.style.display = "none"; //ativar o movimento do camião

        this.activateMotion = 1;
      }
    }
  }

  automaticMovement() {
    //Automatic truck movement
    if (this.activateMotion == 1 && this.activeTrucks.length != 0) {
      for (let i = 0; i < this.activeTrucks.length; i++) {
        // @ts-ignore
        const wareDeparture = this.scene.getObjectByName(this.pathsData_Warehouses.get(this.activeTrucks[i].name)[NetworkComponent.WARE0]); // @ts-ignore

        const wareArrival = this.scene.getObjectByName(this.pathsData_Warehouses.get(this.activeTrucks[i].name)[NetworkComponent.WARE_FINAL]);

        if (wareDeparture != null && wareArrival != null) {
          // @ts-ignore
          let points = this.pathsData.get(this.activeTrucks[i].name).getPoint(this.currentDistance / this.pathLength);

          if (points != undefined) {
            this.activeTrucks[i]?.lookAt(points);
            this.activeTrucks[i]?.position?.set(points.x, points.y, points.z);
            this.currentDistance += this.speed;
            console.log("Departure  : " + wareDeparture?.position.x + " " + wareDeparture?.position.y + " " + wareDeparture?.position.z);
            console.log("Arrival  : " + wareArrival?.position.x + " " + wareArrival?.position.y + " " + wareArrival?.position.z);
            console.log("=========================");
            console.log("Truck : " + this.activeTrucks[i].name + " -> " + this.activeTrucks[i].position.x + " " + this.activeTrucks[i].position.y + " " + this.activeTrucks[i].position.z);
            console.log("\n");

            if (this.currentDistance >= this.pathLength) {
              this.activateMotion = 0;
              this.scene.remove(this.activeTrucks[i]);
              this.pathsData.delete(this.activeTrucks[i].name);
              this.activeTrucks = this.activeTrucks.filter(obj => obj != this.activeTrucks[i]);
              this.truckAudio.stop();
              this.currentDistance = 0;
            }
          }
        }
      }
    }
  }

