  styles: ["*[_ngcontent-%COMP%]{\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\nbody[_ngcontent-%COMP%]{\r\n  margin: 0;\r\n  font-family: \"Lato\", sans-serif;\r\n  background: #EFEEF4;\r\n  line-height: 1.6;\r\n  height: 100vh;\r\n  overflow: hidden;\r\n}\r\n\r\n.container[_ngcontent-%COMP%]{\r\n  width: 100%;\r\n  height: 100%;\r\n  overflow-y: scroll;\r\n  scroll-behavior: smooth;\r\n  scroll-snap-type: y mandatory;\r\n}\r\n\r\n.navbar[_ngcontent-%COMP%]{\r\n  position: fixed;\r\n  top:0;\r\n  z-index: 1;\r\n  display: flex;\r\n  width: 100%;\r\n  height: 60px;\r\n  background: rgba(239,238,244,0.7);\r\n  font-weight: 700;\r\n\r\n}\r\n\r\n.navbar[_ngcontent-%COMP%]   ul[_ngcontent-%COMP%]{\r\n  display: flex;\r\n  list-style: none;\r\n  width: 100%;\r\n  justify-content: center;\r\n}\r\n\r\n.navbar[_ngcontent-%COMP%]   ul[_ngcontent-%COMP%]   li[_ngcontent-%COMP%]{\r\n  margin: 0 1rem;\r\n  padding: 1rem;\r\n  font-family: 'Secular One', sans-serif;\r\n\r\n}\r\n\r\n.navbar[_ngcontent-%COMP%]   ul[_ngcontent-%COMP%]   li[_ngcontent-%COMP%]   a[_ngcontent-%COMP%]{\r\n  text-decoration: none;\r\n  text-transform: uppercase;\r\n\r\n  color: #778DB3;\r\n  font-size: 1.5rem;\r\n\r\n}\r\n\r\n.navbar[_ngcontent-%COMP%]   ul[_ngcontent-%COMP%]   li[_ngcontent-%COMP%]   a[_ngcontent-%COMP%]:hover{\r\n  color: skyblue;\r\n}\r\n\r\n\r\n\r\n.button-73[_ngcontent-%COMP%] {\r\n  background-color: #FFFFFF;\r\n  border-radius: 40em;\r\n  border-style: none;\r\n  box-shadow: #ADCFFF 0 -12px 6px inset;\r\n  box-sizing: border-box;\r\n  color: #000000;\r\n  cursor: pointer;\r\n  font-family: 'Secular One', sans-serif;\r\n  font-size: 1rem;\r\n  font-weight: 600;\r\n  padding: 1rem 1.3rem;\r\n  quotes: auto;\r\n  text-align: center;\r\n  text-decoration: none;\r\n  transition: all .15s;\r\n  user-select: none;\r\n  -webkit-user-select: none;\r\n  touch-action: manipulation;\r\n  margin-left: 47%;\r\n  margin-bottom: 1%;\r\n}\r\n\r\n.button-73[_ngcontent-%COMP%]:hover {\r\n  background-color: #778DB3;\r\n  box-shadow: #37465f 0 -6px 8px inset;\r\n  transform: scale(1);\r\n}\r\n\r\n.button-73[_ngcontent-%COMP%]:active {\r\n  transform: scale(1);\r\n}\r\n\r\n@media (min-width: 768px) {\r\n  .button-73[_ngcontent-%COMP%] {\r\n    font-size: 1rem;\r\n    padding: .75rem 2rem;\r\n  }\r\n}\n/*# sourceMappingURL=data:application/json;base64, */"]
